mapper.select:
select * from over_goods_outStock,sale_order,product_order

      over_goods_outStock.productNum=product_order.productNum and over_goods_outStock.salenum =sale_order.saleNum
    and over_goods_outStock.outStockNumbers = #{outStockNumbers}
    and over_goods_outStock.salenum = #{salenum}
    and sale_order.productNameOrder = #{productNameOrder}
    and sale_order.productSpecificationsOrder = #{productSpecificationsOrder}
    and over_goods_outStock.outFactoryMan = #{outFactoryMan}
    and over_goods_outStock.getOutFactoryTime > #{startDate}

    and over_goods_outStock.getOutFactoryTime<#{endDate}



ORDER BY over_goods_outStock.getOutFactoryTime desc
